MapProvider.Property:
function _createEsriMap(mapElem) { 
    var map = L.map(mapElem.id);
    L.esri.basemapLayer("Topographic").addTo(map);
    return map;
};
CREATE_MAP_CALLBACK = _createEsriMap;

function _createMapBoxMap(mapElem) { 
    L.mapbox.accessToken = MAP_AUTHORIZATION;
    return L.mapbox.map(mapElem.id, 'mapbox.streets');
};
CREATE_MAP_CALLBACK = _createMapBoxMap;
